
public class If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int num1 = 7;
		int num2 = 10;
				
		if (num1 > num2) {
			System.out.println("7 is greater than 10.");
		}
				
		if(num1 < num2) {
			System.out.println("7 is NOT greater than 10.");
		}
		
		if(num1 == num2) {
			System.out.println("7 is equal to 10.");
		}
	}

}
